const RatingStars = ({ value, onChange }) => {
  const stars = [1, 2, 3, 4, 5];

  return (
    <div className="rating-container">
      {stars.map((star) => (
        <span
          key={star}
          className={`star ${star <= value ? "filled" : ""}`}
          onClick={() => onChange(star)}
          title={`${star}`}
        >
          ★
        </span>
      ))}
      {value > 0 && <span style={{marginLeft: '5px', fontSize: '14px'}}>{value}</span>}
    </div>
  );
};

// Componente per un elemento di valutazione (valore + note)
const EvaluationItem = ({ label, ratingField, notesField, formData, setFormData }) => {
  const handleRatingChange = (value) => {
    setFormData({
      ...formData,
      [ratingField]: value
    });
  };

  const handleNotesChange = (e) => {
    setFormData({
      ...formData,
      [notesField]: e.target.value
    });
  };

  return (
    <div className="evaluation-item">
      <div className="evaluation-header">
        <div className="evaluation-label">{label}</div>
        <RatingStars 
          value={formData[ratingField] || 0} 
          onChange={handleRatingChange} 
        />
      </div>
      <textarea
        className="notes-field"
        placeholder="Note..."
        value={formData[notesField] || ""}
        onChange={handleNotesChange}
      />
    </div>
  );
};

